private long firstBackTime;
@Override
public void onBackPressed() {
if (System.currentTimeMillis() - firstBackTime > 2000) {
Toast.makeText(this, "再按一次返回键退出程序", Toast.LENGTH_SHORT).show();
firstBackTime = System.currentTimeMillis();
return;
}

super.onBackPressed();
}

